"    background-color: transparent;     /* Fondo del header view */\n"
"    border: none;                     /* Sin borde */\n"
"}\n"
"\n"
"/* CADA SECCIÓN DE ENCABEZADO (cada columna) */\n"
"QHeaderView::section {\n"
"    background-color: qlineargradient(spread:pad, x1:0.551, y1:0, x2:0.511, y2:1, stop:0 rgba(25, 213, 100, 255), stop:1 rgba(0, 170, 97, 255)) ;        /* Color de fondo */\n"
"    color: white;                     /* Color del texto */\n"
"    border: 1px solid #ffffff;        /* Borde de cada sección */\n"
"    border-left: none;                /* Quitar borde izquierdo */\n"
"    font: 11pt \"Segoe UI\";\n"
"    font-weight: 600;\n"
"    \n"
"    \n"
"    /* Alineación del texto */\n"
"    text-align: center;                 /* left, center, right */\n"
"    \n"
"}\n"
"\n"
"/* Sección cuando se pasa el mouse */\n"
"QHeaderView::section:hover {\n"
"    background-color: qlineargradient(spread:pad, x1:0, y1:0.518, x2:1, y2:0.557, stop:0 rgba(19, 162, 74, 255), stop:1 rgba(0, 136, 77, 255));;\n"
"}\n"
"\n"
"/* Sección cuando se presiona */\n"
"QHeaderView::section:pressed {\n"
"    background-color: qlineargradient(spread:pad, x1:0, y1:0.518, x2:1, y2:0.557, stop:0 rgba(19, 162, 74, 255), stop:1 rgba(0, 136, 77, 255));;\n"
"}\n"
"\n"
"/* Para la última sección (última columna) */\n"
"QHeaderView::section:last {\n"
"    border-right: none;               /* Quitar borde derecho del último */\n"
"}\n"
"\n"
"QHeaderView::section:vertical {\n"
"    color: white;\n"
"    text-align: center;\n"
"    font-size:12px;\n"
"    padding:0px;\n"
"}\n"
"\n"
"QTableView QTableCornerButton::section {\n"
"    background-color: qlineargradient(spread:pad, x1:0.551, y1:0, x2:0.511, y2:1, stop:0 rgba(25, 213, 100, 255), stop:1 rgba(0, 170, 97, 255)) ;        /* Color de fondo */\n"
"    border:1px solid #ffffff;\n"
"}\n"
"\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"\n"
"\n"
"/* 10. QListWidget */\n"
"\n"
"QListWidget{\n"
"    background:rgb(30, 30, 30);\n"
"    border:1px solid #ffffff;\n"
"}\n"
"\n"
"QListWidget::item {\n"
"    /* Propiedades del item */\n"
"    color: #ffffff;\n"
"}\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"\n"
"\n"
"/* 11. QScrollArea */\n"
"QScrollArea {\n"
"        background-color: transparent;\n"
" }\n"
"\n"
" QScrollArea > QWidget > QWidget {\n"
"        background-color: transparent;\n"
" }\n"
"\n"
"\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"\n"
"\n"
"/* 12. QTimeEdit */\n"
"\n"
"/* CONTENEDOR PRINCIPAL */\n"
"QTimeEdit {\n"
"    background: rgb(30, 30, 30);\n"
"    color: rgb(255, 255, 255);\n"
"    font: 14px \"Segoe UI\";\n"
"    font-weight: 500;\n"
"    border-radius: 12px;\n"
"    padding: 4px 6px;\n"
"}\n"
"\n"
"\n"
"/* FOCUS */\n"
"QTimeEdit:focus {\n"
"   border: 2px solid rgb(25, 213, 100);        \n"
"    background-color: rgb(48, 48, 48);\n"
"}\n"
"\n"
"\n"
"/* BOTONES DE SUBIR Y BAJAR */\n"
"QTimeEdit::up-button {\n"
"    width: 35px;\n"
"    border: none;\n"
"    margin:0px;\n"
"    border-top-right-radius:10px;\n"
"    image: url(:/iconos/recursos_de_imagenes/iconos_de_interfaz/chevron-up.png);\n"
"    background:qlineargradient(spread:pad, x1:0, y1:0.545, x2:1, y2:0.545364,             stop:0 rgba(43, 237, 121, 255), stop:1 rgba(0, 170, 97, 255));\n"
"}\n"
"\n"
"\n"
"QTimeEdit::down-button{\n"
"    width: 35px;\n"
"    background: red;\n"
"    border: none;\n"
"    border-bottom-right-radius:10px;\n"
"    image: url(:/iconos/recursos_de_imagenes/iconos_de_interfaz/chevron-down.png);\n"
"background:qlineargradient(spread:pad, x1:0, y1:0.545, x2:1, y2:0.545364,             stop:0 rgba(43, 237, 121, 255), stop:1 rgba(0, 170, 97, 255));\n"
"\n"
"}\n"
"\n"
"\n"
"\n"
"/* HOVER A LOS BOTONES */\n"
"QTimeEdit::up-button:hover {\n"
"    background: rgb(2, 150, 84);\n"
"}\n"
"\n"
"\n"
"QTimeEdit::down-button:hover{\n"
"     background: rgb(2, 150, 84);\n"
"}\n"
"\n"
"QTimeEdit:disabled{\n"
"    background-color: rgb(67, 67, 67);    \n"
"    border: 2px solid rgb(30, 30, 30);\n"
"    color: #000000; \n"
"}\n"
"\n"
"QTimeEdit::up-button:disabled,\n"
"QTimeEdit::down-button:disabled{\n"
"    background-color: rgb(47, 47, 47);\n"
"    color: #000000;\n"
"\n"
"}\n"
"\n"
"\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"\n"
"\n"
"/* 13. QCheckBox */\n"
"\n"
"QCheckBox{\n"
"    color:#ffffff;\n"
"\n"
"}\n"
"\n"
"\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"\n"
"\n"
"/* 14. QGroupBox */\n"
"\n"
"QGroupBox{\n"
"    background:transparent;\n"
"    border:none;\n"
"    color:#ffffff;\n"
"}\n"
"\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"/*--------------------------------------------------------------------------------------------------*/\n"
"\n"
"\n"
"/* 15. QMessageBox */\n"
"\n"
"QMessageBox {\n"
"    background-color: #2b2b2b;\n"
"}\n"
"\n"
"QMessageBox QLabel {\n"
"    color: #ffffff;\n"
"    font-size: 14px;\n"
"    font-weight: bold;\n"
"}\n"
"\n"
"QMessageBox QPushButton {\n"
"    color: white;\n"
"    padding: 8px 16px;\n"
"    border-radius: 4px;\n"
"    font-size: 15px;\n"
"    min-width: 40px;\n"
"}\n"
"\n"
